mapper.select:
SELECT *
 FROM tb_commute c
 JOIN tb_employees e
   ON c.emp_id = e.emp_id
WHERE c.emp_id = #{empId}
  AND c.commute_dt = #{today}

mapper.update:
UPDATE tb_commute 
   SET getoff_time = #{nowTime}
 WHERE emp_id = #{empId}
   AND commute_dt = #{today}

UPDATE tb_commute 
   SET work_time = #{realTime}
 WHERE emp_id = #{empId}
   AND commute_dt = #{today}

UPDATE tb_commute 
   SET added_time = #{addTime}
 WHERE emp_id = #{empId}
   AND commute_dt = #{today}

UPDATE tb_commute 
   SET week_addtime = #{weekAdd}
 WHERE emp_id = #{empId}
   AND commute_dt = #{today}

UPDATE tb_commute 
   SET week_worktime = #{weekWork}
 WHERE emp_id = #{empId}
   AND commute_dt = #{today}
