mapper.select:
SELECT emp_email
  FROM tb_employees
 WHERE emp_id = #{empId}

SELECT emp_nm
  FROM tb_employees
 WHERE emp_email = #{empEmail}

mapper.update:
UPDATE tb_receive_mail
   SET mail_read_time = (SYSDATE  + (interval '9' hour))
   	 , mail_read_fl = 'Y'
 WHERE emp_id = #{empId}
   AND mail_id = #{mailId}

UPDATE tb_receive_mail
   SET mail_read_fl = 'N'
 WHERE mail_id = #{mailId}

UPDATE tb_receive_mail
   SET mail_folder = 3
 WHERE emp_id = #{empId}
   AND mail_id = #{mailId}

UPDATE tb_receive_mail
   SET mail_folder = 1
 WHERE emp_id = #{empId}
   AND mail_id = #{mailId}

UPDATE tb_receive_mail
   SET mail_folder = 4
 WHERE emp_id = #{empId}
   AND mail_id = #{mailId}
